$(document).ready(function () {
    $("#catalogMenu").click(function (e) { 
        e.preventDefault();
        
        $(".dropdown-menu").toggleClass("show");
        $(".dropdown").toggleClass("__custom");
        $(".arrow").toggleClass("open");
        setTimeout(() => {
            $("#catalogMenu").data("hidden", "true");
        }, 1);
    });
    $(document).click(function(){
        let block = $(".dropdown.__custom");
        if (block != null)
            if($("#catalogMenu").data("hidden") === "true"){
                $(".dropdown-menu").toggleClass("show");
                $(".dropdown").toggleClass("__custom");
                $(".arrow").toggleClass("open");
                $("#catalogMenu").data("hidden", "flase");
            }
    });
    
    var box;
    $(".dropdown-item.__item").hover(function () {
            // over
            let item = $(this).data("item");
            
            if(box != null)
                box.css("display", "none");

            box = $(document).find("div._box-catalog[data-item='"+ item +"']");
            console.log(box);
            box.css("display", "flex");
            
            
        }, function () {
            // out
        }
    );
});

